import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.div`
  // Ajoutez vos styles de conteneur de formulaire si nécessaire
`;

const AddRecipeForm = ({ onAddRecipe }) => {
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [preparation, setPreparation] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Créez un nouvel objet de recette avec les données saisies dans le formulaire
    const newRecipe = {
      title,
      ingredients,
      preparation
      // Ajoutez d'autres propriétés si nécessaire
    };
    // Passez la nouvelle recette au gestionnaire onAddRecipe pour l'ajouter au tableau de données
    onAddRecipe(newRecipe);
    // Réinitialisez les champs du formulaire après l'ajout de la recette
    setTitle('');
    setIngredients('');
    setPreparation('');
  };

  return (
    <FormContainer>
      <h2>Ajouter une recette</h2>
      <form onSubmit={handleSubmit}>
        <label>Titre:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <label>Ingrédients:</label>
        <textarea value={ingredients} onChange={(e) => setIngredients(e.target.value)} required />
        <label>Préparation:</label>
        <textarea value={preparation} onChange={(e) => setPreparation(e.target.value)} required />
        <button type="submit">Ajouter</button>
      </form>
    </FormContainer>
  );
};

export default AddRecipeForm;
