import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"

const StyledContainer = styled.div`
    margin: 80px auto;
    padding: 30px;
    width: 1200px;
    height: 80px;
    background-color: black;
`

function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    console.log(recipoe)
    //const recipe = RecipeMockedData[currentId]
    return (
        <div>
            <StyledContainer>
                <h1>{ recipe.title }</h1>
                <ul>{ recipe.ingredients.map( (ingredient, index) => (<li key={index}>{ingredient}</li>))}</ul>
            </StyledContainer>
        </div>
    )
}

export default Recipe
