import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"

const StyledContainer = styled.div`
    margin: 80px auto;
    padding: 30px;
    width: 1200px;
    height: 80px;
    background-color: black;
`

function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    //const recipe = RecipeMockedData[currentId]
    return (
        <div>
            <StyledContainer>
                <h1>{ recipe.title }</h1>
                <ul>{ Object.entries(recipe.ingredients).map(([name, quantity]) => (<li key={name}>{name} : {Object.entries(quantity).map(([key, value]) => (<span key={value.amount}>{value.amount} {value.unit}</span>))}</li>))}</ul>
            </StyledContainer>
        </div>
    )
}

export default Recipe
