import styled from "styled-components";
import { colors } from "../../utils/style/variables";
import MainCourseIcon from '../../assets/poisson.png'
import Dessert from '../../assets/dessert.png'
import Chef from '../../assets/chef.png'
import Drinks from '../../assets/jus.png'
import Preps from '../../assets/soupe.png'

const StyledBackground = styled.div`
    position: absolute;
    top: 0;
    z-index: -3;
    width: 100VW;
    height: 100vh;
    background-color: ${colors.secondary};
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(4vw, 1fr)); /* Chaque colonne fait 4vw de large */
    gap: 0;
    height: 100vh
`

function Background() {
    return (
        <StyledBackground>
            ${MainCourseIcon}
        </StyledBackground>
    )
}

export default Background
