import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"

const StyledContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 20px;
    margin: 80px auto;
    padding: 30px;
    width: 1200px;
    background-color: black;
`

function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    //const recipe = RecipeMockedData[currentId]
    return (
        <div>
            <StyledContainer>
                <h1>{ recipe.title }</h1>
                <div>4ps</div>
                <div>4ps</div>
                <div>4ps</div>
                <ul>{ Object.entries(recipe.ingredients).map(([name, quantity]) => (<li key={name}>
                            {name} : {quantity.amount} {quantity.unit}
                        </li>))}</ul>
            </StyledContainer>
        </div>
    )
}

export default Recipe
