import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"

const StyledContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-gap: 20px;
    margin: 80px auto;
    padding: 30px;
    width: 1200px;
    background-color: black;
`
const Title = styled.h1`
grid-column: span 2;
`
const People = styled.div`
grid-column: span 2;
`
const IngredientsContainer = styled.ul`
    grid-column: span 2;
`
const StepsContainer = styled.ul`
    grid-column: span 2;
`


function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    //const recipe = RecipeMockedData[currentId]
    return (
        <div>
            <StyledContainer>
                <Title >{ recipe.title }</Title>
                <div>4ps</div>
                <div>30mn</div>
                <div>ustensils</div>
                <IngredientsContainer>{ Object.entries(recipe.ingredients).map(([name, quantity]) => (
                    <li key={name}>
                            {name} : {quantity.amount} {quantity.unit}
                    </li>))}
                </IngredientsContainer>
                <StepsContainer>STEPS</StepsContainer>
            </StyledContainer>
        </div>
    )
}

export default Recipe
