import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"
import { colors } from '../../utils/style/variables'
import PlayIcon from '../../assets/play.png'
import { utensils } from "../../utils/Data"

const RecipeContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(7, 1fr);
    grid-template-rows: 1fr 2fr;
    grid-gap: 20px;
    margin: 0 auto;
    padding: 30px;
    max-width: 1200px;
    background-color: black;
    border-radius: 30px;
    max-height: 90vh;
`

const Title = styled.div`
    grid-column: span 3;
    color: ${colors.fourth};
    margin-left: 30px;
    font-size: 25px;
`
const DisplayContainer = styled.div`
    grid-column: span 1;
    border-radius: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 15px;
    cursor: pointer;
    ${(props) => props.$people && `
        border: 2px solid ${colors.fourth};
        `}
    ${(props) => props.$info && `
        font-size: 40px; 
        font-weight: 800; 
        color: white;
        `}
    ${(props) => props.$time && `
        border: 2px solid ${colors.fourth};
        `}
    ${(props) => props.$utensils && `
        border: 2px solid white; 
        background-color: white; 
        color: black;
        `}
    ${(props) => props.$playBtn && `
        background-color: ${colors.fourth};
        `}
`
const Icon = styled.img`
    width: 50%;
`
const IngredientsContainer = styled.div`
    grid-column: span 2;
    list-style-type: none;
    background-color: white;
    padding: 20px;
    border-radius: 30px;
    font-size : 20px;
    letter-spacing: -1px;
    color: black;
    line-height: 1.5;
`
const IngredientsList = styled.ul`
    list-style-type: none;
    padding-left: 0;
`
const StepsContainer = styled.ul`
    grid-column: span 4;
    list-style-type: "-";
    color: white;
    font-size: 25px;
    letter-spacing: -1px;
    margin-top: 45px;
    line-height: 1.5;
`
function calculateDuration(recipe) {
    const { preparation, cooking_duration } = recipe
    const total = (preparation || 0) + (cooking_duration || 0)
    return total
}

function getUtensilIcon(utensilName) {
    if (utensils.hasOwnProperty(utensilName)) {
        return utensils[utensilName]
    } else return null
}
function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    const duration = calculateDuration(recipe)

    return (
        <div>
            <RecipeContainer>
                <Title >
                    <h1>{ recipe.title.toUpperCase() }</h1>
                </Title>
                <DisplayContainer $info $people>{recipe.people}ps</DisplayContainer>
                <DisplayContainer $info $time>{duration}mn</DisplayContainer>
                <DisplayContainer $utensils>

                    {recipe.utensils.map}
                </DisplayContainer>
                <DisplayContainer $playBtn>
                    <Icon src={PlayIcon}/>
                </DisplayContainer>
                <IngredientsContainer>
                    <h2 style={{ marginTop: 0 }}>INGREDIENTS</h2>
                    <IngredientsList>
                        { Object.entries(recipe.ingredients).map(([name, quantity]) => (
                            <li key={name}>
                                    <span style={{fontWeight: 800, color: `${colors.primary}`}}>{name}</span> : {quantity.amount} {quantity.unit}
                            </li>))
                        }
                    </IngredientsList>
                </IngredientsContainer>
                <StepsContainer> 
                    { recipe.steps.map(step => (
                        <li >&nbsp;{step}</li>))
                    } 
                </StepsContainer>
            </RecipeContainer>
        </div>
    )
}

export default Recipe
