import React from "react";
import styled from "styled-components";
import { colors } from "../../utils/style/variables";

import MainCourseIcon from '../../assets/poisson.png'
import Dessert from '../../assets/dessert.png'
import Chef from '../../assets/chef.png'
import Drinks from '../../assets/jus.png'
import Preps from '../../assets/soupe.png'

const IconsArray = [
    Dessert,
    Chef,
    Drinks,
    Preps
]
const getRandomIcon = () => {
    return IconsArray[Math.floor(Math.random() * IconsArray.length)]
}
// STYLE //////////////////////////////////
const StyledBackground = styled.div`
    position: absolute;
    top: 0;
    z-index: -3;
    width: 100VW;
    height: 100vh;
    background-color: ${colors.secondary};
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(6vw, 1fr));
    gap: 0;
    overflow-y: hidden;
    overflow-y: hidden;
`
const Icon = styled.img`
    width: 100%;
    height: auto;
    object-fit: contain
`

function Background() {
    return (
        <StyledBackground>
            {Array.from({ length: Math.ceil((window.innerWidth * window.innerHeight) / (4 * 4)) }).map((_, index) => (
                <Icon key={index} src={getRandomIcon()} alt=''/>
            ))}
        </StyledBackground>
    )
}

export default Background
