import { useParams } from "react-router-dom"
import { RecipeMockedData } from "../../utils/Data"
import styled from "styled-components"

const StyledContainer = styled.div`
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-gap: 20px;
    margin: 80px auto;
    padding: 30px;
    max-width: 1200px;
    background-color: black;
`
const Title = styled.h1`
grid-column: span 2;
`
const People = styled.div`
grid-column: span 1;
`
const Utensils = styled.div`
grid-column: span 1;
`
const Time = styled.div`
grid-column: span 1;
`
const IngredientsContainer = styled.ul`
    grid-column: span 2;
    list-style-type: none;
`
const Steps = styled.ul`
    grid-column: span 3;
    list-style-type: none;
`


function Recipe() {
    const { currentId } = useParams()
    const recipe = RecipeMockedData.find(recipe => recipe.id === parseInt(currentId))
    //const recipe = RecipeMockedData[currentId]
    return (
        <div>
            <StyledContainer>
                <Title >{ recipe.title }</Title>
                <People>{recipe.people} ps</People>
                <Utensils>{recipe.utensils}</Utensils>
                <Time>40mn</Time>
                <IngredientsContainer>{ Object.entries(recipe.ingredients).map(([name, quantity]) => (
                    <li key={name}>
                            {name} : {quantity.amount} {quantity.unit}
                    </li>))}
                </IngredientsContainer>
                <StepsContainer> { recipe.steps.map(step => (
                    <li>{step}</li>))} 
                </StepsContainer>
            </StyledContainer>
        </div>
    )
}

export default Recipe
