import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import AddRecipeForm from '../../components/Form'; // Assurez-vous d'importer correctement votre composant AddRecipeForm
import { RecipeMockedData } from '../../utils/Data'; // Assurez-vous d'importer correctement vos données de recette
import styled from 'styled-components';

const StyledContainer = styled.div`
  // Ajoutez ici vos styles pour le conteneur si nécessaire
`;

const MainTitle = styled.h1`
  text-transform: uppercase; // Pour mettre le texte en majuscules
  // Ajoutez ici vos autres styles pour le titre principal si nécessaire
`;

function AddRecipe() {
  const { type } = useParams();
  const [recipes, setRecipes] = useState(RecipeMockedData);

  const handleAddRecipe = (newRecipe) => {
    // Ajoutez la nouvelle recette à votre tableau de données existant
    setRecipes([...recipes, newRecipe]);
  };

  const filteredRecipes = recipes.filter(recipe => recipe.type === type);

  return (
    <StyledContainer>
      <MainTitle>{type.toUpperCase()}</MainTitle>
      {filteredRecipes.map(recipe => (
        <div key={recipe.id}>
          <img src={recipe.img} alt={recipe.title} />
          <p>{recipe.title}</p>
        </div>
      ))}
      <AddRecipeForm onAddRecipe={handleAddRecipe} />
    </StyledContainer>
  );
}

export default AddRecipe;
