import { createGlobalStyle } from "styled-components"
import { colors } from './'

const StyledGlobalStyle = createGlobalStyle`
    * {
        font-family: "IBM Plex Mono", monospace;
    }
    body {
        background-color: ${colors.dark_background}
    }
`

export default StyledGlobalStyle
